// n8n Function Node: Advanced Prompt Injection Detection
// Detects jailbreak, exfiltration, code exec, obfuscation, social engineering, and multi-stage attacks
// Includes base64/hex/rot13 detection + execution intent + content analysis

const DEFAULT_THRESHOLD = 40;

const rules = [
  // 1) Instruction override
  { re: /(ignore|disregard|forget) (previous|prior|all) (instructions|rules)/i, weight: 20, tag: "override_instructions" },
  { re: /(ignore|disregard|forget)\s+(previous|prior|all)?\s*(instructions?|rules?|context|guardrails?|polic(?:y|ies)?|prompt)/i, weight: 20, tag: "override_instructions_broad" },
  { re: /(you (are|aren't) (now|no longer) (chatgpt|an ai)|pretend to be|roleplay as)/i, weight: 15, tag: "identity_shift" },
  { re: /(developer\s*mode|do\s*anything\s*now|dan\b)/i, weight: 25, tag: "jailbreak_memes" },
  { re: /\b(system\s*prompt|hidden\s*instructions|secret\s*rules|policy\b|api\s*key|api\s*token|auth\s*key)/i, weight: 10, tag: "policy_exfil" },
  { re: /\b(system\s*prompt|prompt\s*system|system\s*instructions?)\b/i, weight: 15, tag: "policy_exfil_flexible" },
  { re: /(override|bypass|disable) (guardrails|safety|content (filters|policy))/i, weight: 18, tag: "safety_bypass" },
  { re: /(ignore|disregard|forget).{0,60}\b(prompt|system|instructions?|rules?)\b/i, weight: 25, tag: "override_near_sensitive_terms" },

  // 2) Exfiltration queries
  { 
    re: /(reveal|show|print|disclose|leak)\s*(your|the)?\s*(system\s*prompt|prompt\s*system|api\s*key|api\s*token|secret|credentials?|auth\s*key)/i, 
    weight: 25, 
    tag: "secret_exfil" 
  },
  { 
    re: /\b(what('?s| is) (your|the) (system|base|initial) (prompt|instructions?)|tell me your (original|default) (rules|behavior))\b/i, 
    weight: 25, 
    tag: "sys_prompt_query" 
  },

  // 3) Code & execution
  { re: /(run|execute|eval|interpret)\s+(this|the)\s+(code|script)/i, weight: 20, tag: "code_exec" },
  { 
    re: /\b(open|read|cat|print|show|access)\s+(\/etc\/passwd|\/etc\/shadow|\/root|\.env|config\.json|secrets?\.txt)\b/i, 
    weight: 20, 
    tag: "file_system_access" 
  },

  // 4) Obfuscation
  { re: /(base64|rot13|hex)\s+(decode|decodify|unpack|convert)|==$/i, weight: 12, tag: "obfuscation" },
  { re: /(ignore|strip)\s+(all|any)\s+(previous|context)\s+and\s+(just|only)\s+(do|follow)/i, weight: 12, tag: "context_strip" },

  // 5) Social engineering
  { re: /(this is urgent|security audit|boss said|legal requires)/i, weight: 6, tag: "pressure_tactics" },
  { re: /(follow the steps exactly|step-by-step)\s+and\s+(don'?t|do not)\s+(question|explain)/i, weight: 10, tag: "authoritative_tone" },
];

const suggestions = [
  "Block prompts attempting to override prior instructions.",
  "Never execute code or scripts upon user request.",
  "Prevent leakage of system prompt, API keys, or internal rules.",
  "Disable file system and network access for LLMs in untrusted flows.",
  "Use response templates and action allowlists.",
  "Monitor for code blocks with dangerous JavaScript or commands."
];

return $input.all().map((item) => {
  const text = (item.json?.message || "").toString();
  const sRaw = text.toLowerCase();
  const sWords = sRaw.replace(/[|\\\-]+/g, ' ').replace(/\s+/g, ' ').trim();

  const hits = [];
  let score = 0;

  // === ðŸ”Ž BASE RULES (on cleaned text) === //
  for (const rule of rules) {
    const match = sWords.match(rule.re);
    if (match) {
      score += rule.weight;
      hits.push({ 
        tag: rule.tag, 
        fragment: match[0].trim(), 
        weight: rule.weight 
      });
    }
  }

  // === ðŸ”¥ STRUCTURAL & CONTEXTUAL RULES (on sRaw) === //

  // Code block
  if (/```[\s\S]*```/.test(sRaw)) {
    const w = 8;
    score += w;
    hits.push({ tag: "code_block_present", fragment: "Code block", weight: w });
  }

  // Suspicious JS syntax in code block
  if (/```(?:js|javascript|node)?[\s\n]*[\s\S]*?(?:console\.log|fetch\(|eval\(|function|const\s+\w+\s*=|let\s+\w+\s*=)/i.test(sRaw)) {
    const w = 20;
    score += w;
    hits.push({ tag: "suspicious_js_codeblock", fragment: "JS syntax", weight: w });
  }

  // Exec command + code block
  if (/(run|execute|eval|interpret)\s+(this|the)\s+(code|script)/i.test(sRaw) && /```[\s\S]*```/.test(sRaw)) {
    const w = 30;
    score += w;
    hits.push({ tag: "exec_directive_plus_codeblock", fragment: "Exec + block", weight: w });
  }

  // Network fetch
  if (/\b(curl|wget)\b\s+https?:\/\/[^\s]+/.test(sRaw)) {
    const w = 12;
    score += w;
    hits.push({ tag: "network_fetch", fragment: "curl|wget", weight: w });
  }

  // Pipe to shell
  if (/\|\s*(bash|sh|zsh|ash|dash|ksh)\b/.test(sRaw)) {
    const w = 16;
    score += w;
    hits.push({ tag: "pipe_to_shell", fragment: "| bash/sh", weight: w });
  }

  // Critical: curl | bash
  if (/\b(curl|wget)\b[\s\S]{0,120}\|\s*(bash|sh)\b/.test(sRaw)) {
    const w = 60;
    score += w;
    hits.push({ tag: "fetch_piped_to_shell", fragment: "curl | bash", weight: w });
  }

  // curl && ./payload
  if (/\b(curl|wget)\b[\s\S]+(&&|\|\|)\s*\.?\//.test(sRaw)) {
    const w = 18;
    score += w;
    hits.push({ tag: "fetch_and_execute", fragment: "curl && ./payload", weight: w });
  }

  // === ðŸ”— MULTI-FACTOR / COMBINATION RULES === //

  // Ignore + prompt system
  if (
    /(ignore|disregard|forget).{0,60}\b(prompt|system|instructions)/i.test(sRaw) &&
    /\b(system\s*prompt|prompt\s*system)\b/i.test(sRaw)
  ) {
    const w = 20;
    score += w;
    hits.push({ tag: "semantic_bypass_combo", fragment: "ignore + prompt", weight: w });
  }

  // Urgency + secret request
  if (
    /(this is urgent|security audit|boss said|legal requires)/i.test(sRaw) &&
    /\b(system\s*prompt|api\s*key|secret|instructions|credentials?)/i.test(sRaw)
  ) {
    const w = 25;
    score += w;
    hits.push({ tag: "social_engineering_combo", fragment: "Urgency + secret", weight: w });
  }

  // Obfuscation + execution intent
  if (
    /(base64|hex|rot13)\s+(decode|unpack|convert)/i.test(sRaw) &&
    /(follow|execute|run|apply|use|perform|carry out)\s+(it|this|the result|that)/i.test(sRaw)
  ) {
    const w = 35;
    score += w;
    hits.push({ 
      tag: "obfuscation_to_execution", 
      fragment: "decode + follow/execute", 
      weight: w 
    });
  }

  // === ðŸ” BASE64 CONTENT ANALYSIS (optional but powerful) === //
  const base64Match = text.match(/[A-Za-z0-9+/=]{20,}/);
  if (base64Match) {
    try {
      const decoded = Buffer.from(base64Match[0], 'base64').toString('utf8');
      if (decoded.length > 5 && decoded.length < 500) {
        const lowerDecoded = decoded.toLowerCase();
        if (/(prompt|system|secret|exec|bash|curl|http|token|key|passwd)/i.test(lowerDecoded)) {
          const w = 40;
          score += w;
          hits.push({ 
            tag: "malicious_base64_content", 
            fragment: `Contains: ${decoded.substr(0, 30)}...`, 
            weight: w 
          });
        }
      }
    } catch (e) { /* ignore invalid base64 */ }
  }

  // === ðŸ“Š CONTEXTUAL PENALTIES === //
  const linkCount = (text.match(/https?:\/\/[^\s)]+/g) || []).length;
  if (linkCount >= 3) {
    const w = 4;
    score += w;
    hits.push({ tag: "many_links", fragment: `${linkCount} links`, weight: w });
  }

  if (text.length > 4000) {
    const w = 6;
    score += w;
    hits.push({ tag: "very_long", fragment: `${text.length} chars`, weight: w });
  }

  // Cap score
  if (score > 100) score = 100;

  const verdict = score >= DEFAULT_THRESHOLD ? "block" : "allow";

  // === ðŸ“¦ FINAL OUTPUT === //
  const result = {
    riskScore: score,
    verdict,
    threshold: DEFAULT_THRESHOLD,
    reasons: [
      hits.length ? `Triggered ${hits.length} threat rule(s).` : "No threats detected.",
      linkCount > 0 ? `${linkCount} link(s) found.` : null,
      text.length > 4000 ? `Message too long (${text.length} chars).` : null
    ].filter(Boolean),
    detectedThreats: hits.map(h => ({
      type: h.tag,
      evidence: h.fragment,
      riskContribution: h.weight
    })),
    input: {
      original: text,
      length: text.length,
      linksDetected: linkCount
    },
    actionSuggested: suggestions
  };

  return { json: result };
});
